import React from 'react';
import './BoardList.css';

const mockPosts = [
    { id: 1, title: '첫 글', author: '관리자', date:'2025-05-28', likes:3, views:37},
    { id: 2, title: '두번째 글', author: 'user1', date:'2025-05-27', likes:5, views:58}
];

const BoardList = () => {
    return (
        <div>
            <h1>POSTS</h1>
            <div className="table-posts">
                <table className="table-board">
                    <thead className="thead-light">
                        <tr className='row-header'>
                            <th scope="col">No.</th>
                            <th scope="col">Title</th>
                            <th scope="col">Date</th>
                            <th scope="col">Likes</th>
                            <th scope="col">Views</th>
                        </tr>
                    </thead>
                    <tbody>
                        {mockPosts.map((post, index) => {
                            <PostItem key={post.id} no={index+1} {...post} />
                        })}
                    </tbody>
                </table>
            </div>
            <a className="btn-primary" href="../">New</a>
        </div>
    )
}

const PostItem = ({no, title, date, likes, views}) => {
    return (
        <tr>
            <td>{no}</td>
            <td><a href='a'>{title}</a></td>
            <td>{date}</td>
            <td>{likes}</td>
            <td>{views}</td>
        </tr>
    )
}

export default BoardList;
